    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String seleccion = jComboBox1.getSelectedItem().toString();
        
       
        
        if ("FACTURA".equals(seleccion)) {
        double consumo = Double.parseDouble(jTextField1.getText());
        double impuesto = .16;
        double total = consumo + .16;
        double servicio = total * .10;
        double totalGeneral = total + servicio;
        jTextArea1.setText("Consumo: " + consumo + "\n" + "Impuesto: " + impuesto + "\n" + "Total: " + total + "\n" + "Servicio: " + servicio + "\n" + "Total General:" + totalGeneral);
        
            } else if ("BOLETA".equals(seleccion)) {
        double consumo = Double.parseDouble(jTextField1.getText());
        double totalFactura = consumo + .16; 
        double total = consumo + .16;
        double servicio = total * .10 ;
        double totalGeneral = total + servicio;
        

                
         jTextArea1.setText("Total: " + totalFactura + "\n" + "Servicio: " + servicio + "\n" + "Total General: " + totalGeneral);
            }

    }//GEN-LAST:event_jButton1ActionPerformed

